import "./write.css"
import plus from "../image/plus-image.png"
import GetContent from '../home/Home'
export default function Write() {

  const INSERT_CONTENT = gql`
  mutation MyMutation($object: myBlog_content_insert_input = {}) {
    insert_myBlog_content_one(object: $object) {
      id
    }
  }
  `;

  const [insertNewData,{data:newData}]=useMutation(INSERT_CONTENT)

  const tambahData = (newContent) => {
    insertNewData({
      variables: {
        object: {
          title:newContent.title,
          stories:newContent.stories,
          image_url:newContent.image_url,
        },
      },
      refetchQueries: [
        {
          query: GetContent,
        },
      ],
    });
  };

    return (
      <div className="write">
      <img
      className="writeImg"
      src="https://images.pexels.com/photos/6685428/pexels-photo-6685428.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500"
      alt=""
      />
      <form className="writeForm">
      <div className="writeFormGroup">
        <label htmlFor="fileInput">
          <i className="writeIcon fas fa-plus">
            <img src={plus} alt="" width="100%" height="auto" />
          </i>
        </label>
        <input id="fileInput" type="file" style={{ display: "none" }} />
        <input
          className="writeInput"
          placeholder="Title"
          type="text"
          autoFocus={true}
        />
      </div>
      <div className="writeFormGroup">
        <textarea
          className="writeInput writeText"
          placeholder="Tell your story..."
          type="text"
          autoFocus={true}
        />
      </div>
      <button className="writeSubmit" type="submit">
        Publish
      </button>
    </form>
      </div>
    )
}
